package Modelo;

import Clases.Persona;
import DAO.DAO;
import java.util.Date;
import java.util.Scanner;
import javax.persistence.EntityManager;

public class App {

    public static void main(String[] args) {
        ConexionOBD conexionOBD = new ConexionOBD("miUnidadPersistencia");

        try {
            EntityManager entityManager = conexionOBD.getEM();

            Scanner scanner = new Scanner(System.in);

            System.out.println("Seleccione una opci칩n:");
            System.out.println("1. Insertar Persona");
            System.out.println("2. Eliminar Persona");
            System.out.println("3. Modificar Persona");

            int opcion = scanner.nextInt();

            switch (opcion) {
                case 1:
                    DAO.insertar(entityManager, Persona.class);
                    break;
                case 2:
                    DAO.delete(entityManager, Persona.class);
                    // L칩gica para eliminar persona
                    break;
                case 3:
                    DAO.update(entityManager, Persona.class);
                    break;
                default:
                    System.out.println("Opci칩n no v치lida");
            }

            entityManager.close();

        } catch (Exception e) {
            System.out.println(e);

        }
    }
}
